import React, { Component } from 'react';
import './LoginPage.css';
import axios from 'axios';
import url from "./url";
import MainContainer from "./MainContainer";
import queryString from 'query-string';
import {Cookies} from 'react-cookie';
import { Redirect } from 'react-router-dom'

class LoginPage extends Component {
    constructor(props) {
        super(props);
        this.state = {user_id:null};
      }
    componentDidMount() {
        const token = queryString.parse(this.props.location.search).token;
        axios.get(url+"login/"+token)
        .then(
          (result) => {
            let new_state=this.state;
            new_state.user_id=result.data.result;
            this.setState({
              new_state
            });
            this.getUserData(result.data.result);
          },
          (error) => {
            console.log("Error 404 : Invalid Token");
          }
        );

      }

    getUserData=(user_id)=>{
      axios.get(url+"users/students/"+user_id)
      .then(
        (result) => {
          const cookies = new Cookies();
          cookies.set('user_id', result.data.result.id, {maxAge : 60*15 });
          cookies.set('user_name', result.data.result.name, {maxAge : 60*15 });
          cookies.set('user_vows', result.data.result.vows, {maxAge : 60*15 });
          return <Redirect to='/' />
        },
        (error) => {
          console.log('Error: Bad Request');
        }
      );
    }

    getState=()=>{
        return this.state;
    }

    onSubmit=()=>{
        this.props.handlePageChange("questionnaire");//ajouter une requête
    }

    handleInputChange=(event)=>{
        const target = event.target;
        const value =  target.value;
        const name = target.name;

        let new_state= this.state;
        new_state[name]=value;
        this.setState(new_state);
    }

    render() {
      return (
        <div></div>
      );
    }
  }


export default LoginPage;
