import React, { Component } from 'react'
import data from "./DummyData";
import './Components.css';

class MainContainer extends Component {
    constructor(props) {
        super(props);
        this.state = { page: "questionnaire" };
      }

    handlePageChange = page_name => {
        let new_state=this.state;
        new_state.page=page_name;
        this.setState(new_state);
    }
  render() {
      let page=null;
      if(this.state.page==="questionnaire"){
          page=<Questionnaire/>
      }
    return (
      <div className="main-container">{page}</div>
    );
  }
}

class LoginPage extends Component {
  render() {
    return (
      <div>nothing for the moment</div>
    );
  }
}

class Questionnaire extends Component {
    constructor(props) {
        super(props);
        this.state = { step: "ranking_beginner_languages" };
      }

    handleStepChange = step_name => {
        let new_state=this.state;
        new_state.step=step_name;
        this.setState(new_state);
    }

    render() {
        let step=null;
        if(this.state.step==="ranking_beginner_languages"){
            let list_beginner_courses=data.courses.filter(function(value, index, arr){return value.categories.includes("DÃ©butant")});
            step=<CourseRankingQuestion courses={list_beginner_courses}/>;
        }
      return (
        <div className="questionnaire">{step}</div>
      );
    }
  }

class BeginLanguageQuestion extends Component {
    render() {
      return (
        <div className="question">
        <Button onClick={()=>this.props.handleStepChange()} text="Oui"/>
        <Button onClick={()=>this.props.handleStepChange()} text="Non"/>
        </div>
      );
    }
  }

class BeginLanguageChoice extends Component {
    render() {
      return (
        <div className="question">
        <Button onClick={()=>this.props.handleStepChange()} text="Suivant"/>
        </div>
      );
    }
  }

//Classes utiles
class Button extends Component {
    render() {
      return (
        <button className="button" onClick={this.props.onClick}>{this.props.text}</button>
      );
    }
  }

class CourseRankingQuestion extends Component {
    constructor(props) {
        super(props);
        this.state = { ranking: [], selection: null};
      }

    handleSelection = selected_course =>{
        let new_state=this.state;
        new_state.selection=selected_course;
        this.setState(new_state);
    }

    handleAddCourseToRanking = course =>{
        if(!(this.state.ranking.includes(course))){
            let new_state=this.state;
            new_state.ranking.push(course);
            new_state.selection=null;
            this.setState(new_state);
        }
    }

    handleRankUp = (index_in_ranking) =>{//fait gagner une place dans le classement
        if(index_in_ranking>0){
            let new_state=this.state;
            let course_to_move=this.state.ranking[index_in_ranking];//swap
            new_state.ranking[index_in_ranking]=new_state.ranking[index_in_ranking-1];
            new_state.ranking[index_in_ranking-1]=course_to_move;
            this.setState(new_state);
        }
    }

    handleRankDown = (index_in_ranking) =>{//fait perdre une place dans le classement
        if(index_in_ranking<this.state.ranking.length-1){
            let new_state=this.state;
            let course_to_move=this.state.ranking[index_in_ranking];//swap
            new_state.ranking[index_in_ranking]=new_state.ranking[index_in_ranking+1];
            new_state.ranking[index_in_ranking+1]=course_to_move;
            this.setState(new_state);
        }
    }

    handleDeletionFromRanking =(index_in_ranking)=>{
        let new_state=this.state;
        new_state.ranking.splice(index_in_ranking, 1);
        this.setState(new_state);
    }

    render() {
      return (
        <div className="course-ranking-question">
            <CourseList courses={this.props.courses} handleSelection={this.handleSelection} handleAddCourseToRanking={this.handleAddCourseToRanking}/>
            <CourseRanking ranking= {this.state.ranking} handleRankUp={this.handleRankUp} handleRankDown={this.handleRankDown} handleDeletionFromRanking={this.handleDeletionFromRanking}/>
        </div>
      );
    }
  }

class CourseRanking extends Component {
    render() {
        let ranking_display=[];
        for(let index=0; index<this.props.ranking.length; index++){
            ranking_display.push(
            <CourseBox course={this.props.ranking[index]} buttons={[
                <Button text="+" onClick={()=>this.props.handleRankUp(index)}/>,
                <Button text="-" onClick={()=>this.props.handleRankDown(index)}/>,
                <Button text="x" onClick={()=>this.props.handleDeletionFromRanking(index)}/>]}/>
            );
        }
      return (
        <div className="course-ranking">{ranking_display}</div>
      );
    }
  }

class CourseList extends Component {
    render() {
        let list_display=[];
        for(let index=0; index<this.props.courses.length; index++){
            list_display.push(
            <CourseBox course={this.props.courses[index]} buttons={[
                <Button text="ajouter au classement" onClick={()=>this.props.handleAddCourseToRanking(this.props.courses[index])}/>
                ]}/>
            );
        }
      return (
        <div className="course-list">{list_display}</div>
      );
    }
  }

class CourseBox extends Component {
    render() {
      return (
        <div className="course-box"><h2>{this.props.course.name}</h2> <p>{this.props.course.schedules.map(date => date+'/n')}</p>{this.props.buttons}</div>
      );
    }
  }

export default MainContainer;
