import React, { Component } from 'react'
import data from "./DummyData";
import './Components.css';

class MainContainer extends Component {
    constructor(props) {
        super(props);
        this.state = { page: "questionnaire" };
      }

    handlePageChange = page_name => {
        let new_state=this.state;
        new_state.page=page_name;
        this.setState(new_state);
    }
  render() {
      let page=null;
      if(this.state.page==="questionnaire"){
          page=<Questionnaire/>
      }
    return (
      <div className="main-container">{page}</div>
    );
  }
}

class LoginPage extends Component {
  render() {
    return (
      <div>nothing for the moment</div>
    );
  }
}

class Questionnaire extends Component {
    constructor(props) {
        super(props);
        this.state = { step: "ranking_beginner_courses", step_list: ["initial_questions"], step_index: 0};
      }

    handleStepChange = step_name => {
        let new_state=this.state;
        new_state.step=step_name;
        this.setState(new_state);
    }

    handleNextStep = ()=>{
      if(this.state.step_index<this.state.step_list.length-1){
        this.setState({step_index: this.state.step_index+1});
      }
    }
    handlePreviousStep = ()=>{
      if(this.state.step_index>0){
        this.setState({step_index: this.state.step_index+1});
      }
    }

    setStepList= (new_step_list)=>{
      this.setState({step_list: new_step_list});
    }

    getQuestionnaireState=()=>{ return this.state;}

    render() {
        let step=null;
        let current_step_name=this.state.step_list[this.state.step_index];
        if(current_step_name==="initial_questions"){
          step=<InitialQuestions setStepList={this.setStepList} getQuestionnaireState={this.getQuestionnaireState} handleStepChange={this.props.handleStepChange}/>;
        }else if(current_step_name==="ranking_beginner_courses"){
            let list_beginner_courses=data.courses.filter(function(value, index, arr){return value.categories.includes("Débutant")});
            step=<CourseRankingQuestion courses={list_beginner_courses} getQuestionnaireState={this.getQuestionnaireState} handleStepChange={this.props.handleStepChange} />;
        }else if(current_step_name==="ranking_english_courses"){
            let list_english_courses=data.courses.filter(function(value, index, arr){return value.categories.includes("Anglais")});
            step=<CourseRankingQuestion courses={list_english_courses} getQuestionnaireState={this.getQuestionnaireState} handleStepChange={this.props.handleStepChange} />;
        }else if(current_step_name==="ranking_spanish_courses"){
          let list_english_courses=data.courses.filter(function(value, index, arr){return value.categories.includes("Espagnol")&&value.categories.includes("intermédiaire")});
          step=<CourseRankingQuestion courses={list_english_courses} getQuestionnaireState={this.getQuestionnaireState} handleStepChange={this.props.handleStepChange} />;
        }else if(current_step_name==="ranking_german_courses"){
          let list_english_courses=data.courses.filter(function(value, index, arr){return value.categories.includes("Allemand")&&value.categories.includes("intermédiaire")});
          step=<CourseRankingQuestion courses={list_english_courses} getQuestionnaireState={this.getQuestionnaireState} handleStepChange={this.props.handleStepChange} />;
        }
      return (
        <div className="questionnaire">{step}</div>
      );
    }
  }

