class CourseRankingQuestion extends Component {
    constructor(props) {
        super(props);
        this.state = { ranking: [], selection: null};
      }

    handleSelection = selected_course =>{
        let new_state=this.state;
        new_state.selection=selected_course;
        this.setState(new_state);
    }

    handleAddCourseToRanking = course =>{
        if(!(this.state.ranking.includes(course))){
            let new_state=this.state;
            new_state.ranking.push(course);
            new_state.selection=null;
            this.setState(new_state);
        }
    }

    handleRankUp = (index_in_ranking) =>{//fait gagner une place dans le classement
        if(index_in_ranking>0){
            let new_state=this.state;
            let course_to_move=this.state.ranking[index_in_ranking];//swap
            new_state.ranking[index_in_ranking]=new_state.ranking[index_in_ranking-1];
            new_state.ranking[index_in_ranking-1]=course_to_move;
            this.setState(new_state);
        }
    }

    handleRankDown = (index_in_ranking) =>{//fait perdre une place dans le classement
        if(index_in_ranking<this.state.ranking.length-1){
            let new_state=this.state;
            let course_to_move=this.state.ranking[index_in_ranking];//swap
            new_state.ranking[index_in_ranking]=new_state.ranking[index_in_ranking+1];
            new_state.ranking[index_in_ranking+1]=course_to_move;
            this.setState(new_state);
        }
    }

    handleDeletionFromRanking =(index_in_ranking)=>{
        let new_state=this.state;
        new_state.ranking.splice(index_in_ranking, 1);
        this.setState(new_state);
    }

    render() {
      return (
        <div className="course-ranking-question">
            <CourseList courses={this.props.courses} handleSelection={this.handleSelection} handleAddCourseToRanking={this.handleAddCourseToRanking}/>
            <CourseRanking ranking= {this.state.ranking} handleRankUp={this.handleRankUp} handleRankDown={this.handleRankDown} handleDeletionFromRanking={this.handleDeletionFromRanking}/>
            <Footer handleStepChange={this.props.handleStepChange} getQuestionnaireState={this.props.getQuestionnaireState}/>
        </div>
      );
    }
  }

