      let left_button=null;
      let right_button=null;
      if(current_step_index>0){
        left_button=<Button text="Précédent" onClick={()=>this.props.handleStepChange(step_list[current_step_index-1])}/>
      }
      if(current_step_index<step_list.length-1){
        right_button=<Button text="Suivant" onClick={()=>this.props.handleStepChange(step_list[current_step_index+1])}/>
      }
      return (
        <div className="footer"> <div className="footer-left"> {left_button}</div><div className="footer-right">{right_button}</div></div>
      );
    }
  }



