import React, { Component } from 'react';
import './LoginPage.css';
import axios from 'axios';
import url from "./url";
import queryString from 'query-string';
import { Cookies } from 'react-cookie';

class LoginPage extends Component {
    constructor(props) {
        super(props);
        this.state = {user_id:null};
      }
    componentDidMount() {
        const token = queryString.parse(this.props.location.search).token;
        axios.get(url+"login/"+token)
        .then(
          (result) => {
            const cookies = new Cookies();
            cookies.set('user_id', result.id, {maxAge : 60*15 });
            cookies.set('user_name', result.name, {maxAge : 60*15 });
            cookies.set('user_vows', result.vows, {maxAge : 60*15 });
          },
          (error) => {
            console.log("Erreur : bad request");
          }
        );
        axios.get(url+"users/students/"+this.state.user_id)
        .then(
          (result) => {
            let new_state=this.state;
            new_state.user_id=result.data.result
            new_state.is_loaded=true
            this.setState({
              new_state
            });
          },
          (error) => {
            this.setState({
              is_loaded: false
            });
          }
        );
      }
    getState=()=>{
        return this.state;
    }

    onSubmit=()=>{
        this.props.handlePageChange("questionnaire");//ajouter une requête
    }

    handleInputChange=(event)=>{
        const target = event.target;
        const value =  target.value;
        const name = target.name;

        let new_state= this.state;
        new_state[name]=value;
        this.setState(new_state);
    }

    render() {
      return (
        <div></div>
      );
    }
  }


export default withCookies(LoginPage);
