import './App.css';

class MainContainer extends Component {
  constructor(props) {
      super(props);
      this.state = { page: "cours" };
    }

  handlePageChange = page_name => {
      let new_state=this.state;
      new_state.page=page_name;
      this.setState(new_state);
  }
render() {
    let page=null;
    if(this.state.page==="cours"){
        page=<cours/>
    }
  return (
    <div className="main-container">{page}</div>
  );
}
}

class LoginPage extends Component {
  render() {
    return (
      <div>nothing for the moment</div>
    );
  }
}

class Cours extends Component {
  constructor(props) {
      super(props);
      this.state = {step_list: ["initial_questions"], step_index: 0, answers: { year: '', TOEIC: '', justification_no_english: '', justification_no_english_text: '', number_english_courses: 1, number_other_courses: 0, english_courses_ranking:[], other_courses_ranking:[]}};
    }
  buildStepList=(answers)=>{
    let step_list=["initial_questions"];
    if(answers.number_english_courses>0){
      step_list.push("ranking_english_courses");
    }
    if(answers.number_other_courses>0){
      step_list.push("ranking_other_courses");
    }
    return step_list
  }

  getStepIndex=()=>{return this.state.step_index;}


  getStepList=()=>{return this.state.step_list;}

  setStepList=(new_step_list)=>{this.setState({step_list: new_step_list});}

  getAnswers=()=>{ return this.state.answers;}

  setAnswers=(new_answers)=>{
    let new_step_list=this.buildStepList(new_answers);
