import React, { Component } from 'react';
import './App.css';
import data from "./DummyData";
import axios from 'axios';
import url from "./url";
import "./MainContener.css"

class MainContainer extends Component {
    constructor(props) {
        super(props);
        this.state = { page: "Cours",data:data };
      }
      async componentDidMount() {
        const { data: data } = await axios.get(url + "/course");
        this.setState({ data });

      }
    handlePageChange = page_name => {
        let new_state=this.state;
        new_state.page=page_name;
        this.setState(new_state);

    }
    get_data = ()=> {
        return this.state.data;
    } 
    delete_course = index =>{ 
      let new_state = this.state;
      new_state.data.courses.splice(index,1);
      this.setState(new_state);
    }
    //edit_course = course_info =>{
    //  course_info
    //}
  render() {
      let page=null;
      if(this.state.page==="Cours"){
          page=<Cours get_data ={this.get_data} delete_course={this.delete_course}/>
      } else if(this.state.page==="login"){
        page=<LoginPage handlePageChange={this.handlePageChange}/>
      }
    return (
      <div className="main-container">{page}</div>
    );
  }
  }

  class LoginPage extends Component {
    render() {
      return (
        <div>nothing for the moment</div>
      );
    }
  }

  class Cours extends Component {
    render() {
      let newdata=this.props.get_data()
        let cours_L=[]
        for(let index=0; index<newdata.courses.length; index++){
          let quit_button=<Button text="x" onClick={()=>this.props.delete_course(index)}/>
          let course_info=[]
            course_info.push(<div  className="cours">
            <h1 className="name">{newdata.courses[index].name}</h1>
            <p className="language">langue : {newdata.courses[index].language}</p>
            {newdata.courses[index].schedules.map((schedule)=>{return <p> horaires : {schedule}</p>})}
            <p>niveau : {newdata.courses[index].level}</p>
            </div>)
            //let change_button=<Button text="edit" onClick={()=>this.props.edit_course(course_info)}/>
            course_info.push([quit_button])
            course_info[1].push([])
          cours_L.push(<div className="course_info">{course_info}</div>)
          }
      return (
        <div className="cours_L">{cours_L}</div>
      );
    }
  }

  class Button extends Component {
    render() {
      return (
        <button className="button" onClick={this.props.onClick}>{this.props.text}</button>
      );
    }
  }

  class box extends Component {
    render() {
      return (
        <input
            name={this.props.name}
            type={this.props.type}
            value={this.props.value}
            onChange={this.props.onChange} />
      )
    }
  }
  export default MainContainer;
